o "-".$month."-".$year;?>' ;        var today = '<?php echo $today;?>';        if(parseDate(selected_date) < parseDate(today))            {                alert('You have choosen passed dates!');                return false;            }            else            {     $.ajax({                  type: 'get',                  url: '<?php echo base_url(); ?>usercalendar/reserveDay/<?php echo $year;?>/<?php echo $month;?>/<?php echo $this->uri->segment(5); ?>/' + day,                  dataType: 'html',                  success: function (html) {                                                     $('#click-date_' + dayf).html(html);                  }                });                }}function parseDate(str){    var s = str.split(" "),        d = str[0].split("-")    return d[2] + d[1] + d[0];}//// Any source code blocks look like this//        t1="10/10/2006" ;        t2="15/10/2006";    //Total time for one day        var one_day=1000*60*60*24; //Here we need to split the inputed dates to convert them into standard format        var x=t1.split("/");             var y=t2.split("/");  //date format(Fullyear,month,date)         var date1=new Date(x[2],(x[1]-1),x[0]);          var date2=new Date(y[2],(y[1]-1),y[0])        var month1=x[1]-1;        var month2=y[1]-1;                //Calculate difference between the two dates, and convert to days                       var diff=Math.ceil((date2.getTime()-date1.getTime())/(one_day)); //        alert(diff);//_Diff gives the diffrence between the two dates.</script>